mapper.insert:
INSERT INTO BG_IMAGE(image_idx, pidx
, main
, detail1
, detail2
, detail3
, showing1
, showing2
, showing3
, showing4
, showing5
)
VALUES(#{image_idx}, #{pidx}
, #{main}
, #{detail1}
, #{detail2}
, #{detail3}
, #{showing1}
, #{showing2}
, #{showing3}
, #{showing4}
, #{showing5}

)

       SELECT MAX(image_idx)+1 FROM BG_IMAGE

mapper.select:
SELECT * FROM (SELECT rownum AS rnum, A.* FROM (SELECT * FROM bg_product pr JOIN bg_image im ON im.pidx = pr.pidx WHERE 1=1
AND kind='R' AND kind='E' AND kind='N' AND kind='B'
ORDER BY pr.rdate desc) A WHERE rownum <= #{lastPost} ) B WHERE rnum >= #{startPost}

mapper.update:
UPDATE bg_product SET pname=#{pname}, price=#{price}, discount=#{discount}, saleprice=#{saleprice}
, saleyn=#{saleyn}, kind=#{kind}, rdate=sysdate
WHERE pidx = #{pidx}

UPDATE bg_option SET oname=#{oname}, addprice=#{addprice}, color=#{color}, sizes=#{sizes}, stock=#{stock}
, texture=#{texture}, saleyn=#{saleyn}
WHERE oidx = #{oidx}

UPDATE bg_image SET pidx=#{pidx}
, main = #{main}
, detail1 = #{detail1}
, detail2 = #{detail2}
, detail3 = #{detail3}
, showing1 = #{showing1}
, showing2 = #{showing2}
, showing3 = #{showing3}
, showing4 = #{showing4}
, showing5 = #{showing5}
WHERE pidx = #{pidx}
