mapper.select:
SELECT count(pidx) 
FROM bg_product 
WHERE kind=#{kind}

	
	
			FROM oro
			RIGHT OUTER JOIN pi
			ON oro.pidx=pi.pidx
			GROUP BY pi.pidx,pi.main,pi.kind,pi.pname,pi.discount,pi.saleprice,pi.price,pi.rdate
			ORDER BY sum(oro.quantity)
			DESC nulls last
		) A 
		WHERE rownum <= #{end}
	)B
	WHERE rnum >= #{start}



		FROM oro
		RIGHT OUTER JOIN pi
		ON oro.pidx=pi.pidx
		GROUP BY pi.pidx,pi.main,pi.kind,pi.pname,pi.discount,pi.saleprice,pi.price,pi.rdate
		ORDER BY pi.rdate
		DESC nulls last
	) A 
	WHERE rownum <= #{end}
)B
WHERE rnum >= #{start}



		FROM oro
		RIGHT OUTER JOIN pi
		ON oro.pidx=pi.pidx
		WHERE pi.kind= #{kind}
		GROUP BY pi.pidx,pi.main,pi.kind,pi.pname,pi.discount,pi.saleprice,pi.price,pi.rdate
		ORDER BY pi.rdate
		DESC nulls last
	) A 
	WHERE rownum <= #{end}
)B
WHERE rnum >= #{start}



		FROM oro
		RIGHT OUTER JOIN pi
		ON oro.pidx=pi.pidx
		WHERE pi.kind= #{kind}
		GROUP BY pi.pidx,pi.main,pi.kind,pi.pname,pi.discount,pi.saleprice,pi.price,pi.rdate
		ORDER BY sum(oro.quantity)
		DESC nulls last
	) A 
	WHERE rownum <= #{end}
)B
WHERE rnum >= #{start}



		FROM oro
		RIGHT OUTER JOIN pi
		ON oro.pidx=pi.pidx
		WHERE pi.kind= #{kind}
		GROUP BY pi.pidx,pi.main,pi.kind,pi.pname,pi.discount,pi.saleprice,pi.price,pi.rdate
		ORDER BY pi.saleprice
		DESC nulls last
	) A 
	WHERE rownum <= #{end}
)B
WHERE rnum >= #{start}



		FROM oro
		RIGHT OUTER JOIN pi
		ON oro.pidx=pi.pidx
		WHERE pi.kind= #{kind}
		GROUP BY pi.pidx,pi.main,pi.kind,pi.pname,pi.discount,pi.saleprice,pi.price,pi.rdate
		ORDER BY pi.saleprice
		ASC nulls last
	) A 
	WHERE rownum <= #{end}
)B
WHERE rnum >= #{start}
