mapper.select:
SELECT count(ridx)
FROM bg_review
WHERE midx=#{midx}

SELECT * FROM (SELECT rownum AS rnum, A.* FROM (SELECT * from rv_Paging WHERE midx=#{midx}
AND rdate >= to_char(sysdate) AND rdate >= to_char(add_months(sysdate,-1)) AND rdate >= to_char(add_months(sysdate,-3)) AND rdate >= to_char(add_months(sysdate,-6)) AND rdate >= to_char(sysdate-365) AND rdate >= to_char(sysdate-(365*3)) AND rdate BETWEEN TO_DATE(#{scri.rdate1},'YYYY-MM-DD') AND TO_DATE(#{scri.rdate2},'YYYY-MM-DD')
)A WHERE rownum <= #{lastPost} )B WHERE rnum >= #{startPost}
