mapper.update:
UPDATE bg_delivery
SET invoice_num=#{invoice_num}
WHERE order_idx=#{order_idx}

mapper.select:
SELECT d.didx, o.order_idx, d.invoice_num, u.uname, d.send_day, d.deli_com, d.deli_stat FROM bg_delivery d RIGHT JOIN bg_order o ON d.order_idx=o.order_idx
INNER JOIN bg_user u ON o.midx=u.midx
WHERE d.deli_stat=#{scri.kind} ORDER BY d.didx DESC LIMIT #{startPost},#{scri.perPageNum}

SELECT o.order_idx, d.didx, u.uname, DATE_FORMAT(o.rdate,'%Y-%m-%d') AS 'rdate', d.send_day, d.deli_com, d.deli_stat, d.cancel, d.refund, d.exchange FROM bg_order o LEFT OUTER JOIN bg_delivery d ON o.order_idx=d.order_idx
INNER JOIN bg_user u ON o.midx=u.midx
WHERE d.cancel=#{scri.kind} ORDER BY o.order_idx DESC LIMIT #{startPost},#{scri.perPageNum}
