mapper.select:

	SELECT * 
	FROM (
		SELECT rownum as rnum, A.* 
		FROM (
			SELECT nidx,subject,rdate,type,imgfile,imges
			FROM bg_notice	
			ORDER BY RDATE DESC
		) A 
		WHERE rownum <= #{lastPost}
		
	
		AND subject LIKE '%'||#{scri.keyword}||'%'
	
		
	)B
	WHERE rnum >= #{startPost}
